import{openDB}from"idb";const initDB=async()=>openDB("jate",1,{upgrade(t){t.objectStoreNames.contains("jate")?console.log("jate database already exists"):(t.createObjectStore("jate",{keyPath:"id",autoIncrement:!0}),console.log("jate database created"))}});export const putDb=async t=>{try{const e=(await initDB()).transaction("jate","readwrite"),a=e.objectStore("jate");await a.clear(),await a.put({content:t}),await e.complete,console.log("Content added to database")}catch(t){console.error("Error adding content to database:",t)}};export const getDb=async()=>{try{const t=(await initDB()).transaction("jate","readonly"),e=t.objectStore("jate"),a=await e.getAll();return await t.complete,a.length>0?a[0].content:null}catch(t){return console.error("Error getting content from database:",t),null}};initDB();