import{getDb,putDb}from"./database.js";import{header}from"./header.js";export default class{constructor(){const e=localStorage.getItem("content");if("undefined"==typeof CodeMirror)throw new Error("CodeMirror is not loaded");this.editor=CodeMirror(document.querySelector("#main"),{value:"",mode:"javascript",theme:"monokai",lineNumbers:!0,lineWrapping:!0,autofocus:!0,indentUnit:2,tabSize:2}),getDb().then((t=>{console.info("Loaded data from IndexedDB, injecting into editor"),this.editor.setValue(t||e||header)})),this.editor.on("change",(()=>{localStorage.setItem("content",this.editor.getValue())})),this.editor.on("blur",(()=>{console.log("The editor has lost focus"),putDb(localStorage.getItem("content"))}))}}